import Card from "./cards";

export default function App() {
  const price = [
    {
      plan: "Intro",
      price: "$19",
      desc: "for most business that want for optimize web queries",
      item: [
        "limited links",
        "Own analytics platform",
        "Chat support",
        "Optimize hashtags",
        "Unlimited users",
      ],
    },
    {
      plan: "Popular",
      price: "$39",
      desc: "for most business that want for optimize web queries",
      item: [
        "35 limited links",
        "Own analytics platform",
        "Chat support",
        "Optimize hashtags",
        "Unlimited users",
      ],
    },
    {
      plan: "Enterprise",
      price: "$99",
      desc: "for most business that want for optimize web queries",
      item: [
        "All limited links",
        "Own analytics platform",
        "Chat support",
        "Optimize hashtags",
        "Unlimited users",
      ],
    },
  ];
  return (
    <div>
      {price.map((p) => {
        if (p.plan === "Intro") {
          return (
            <>
              <Card
                plan={p.plan}
                price={p.price}
                desc={p.desc}
                item={p.item.map((i) => {
                  return i;
                })}
              ></Card>
            </>
          );
        } else if (p.plan === "Popular") {
          return (
            <>
              <Card
                plan={p.plan}
                price={p.price}
                desc={p.desc}
                item={p.item.map((i) => {
                  return i;
                })}
              ></Card>
            </>
          );
        } else if (p.plan === "Enterprise") {
          return (
            <>
              <Card
                plan={p.plan}
                price={p.price}
                desc={p.desc}
                item={p.item.map((i) => {
                  return i;
                })}
              ></Card>
            </>
          );
        }
      })}
    </div>
  );
}
