export default function Card(props) {
  function starsCount(stars) {
    let count = 5;
    let ratings = [];
    while (stars <= count) {
      ratings.push(<i className="fas fa-star"></i>);
      count--;
    }
    // ratings.length <= 5 ? ratings.push(<i className="far fa-star"></i>) : "";

    return ratings;
  }
  return (
    <div className="col-lg-4">
      <div className="card my-2">
        <img src={props.data.img} className="card-img-top" alt="..." />
        <div className="card-body">
          <h4 className="card-title text-primary">{props.data.title}</h4>
          <h5 className="card-title font-weight-bold">${props.data.price}</h5>
          <p className="card-text">{props.data.desc}</p>
          <div className="card__footer d-flex justify-content-between">
            <span className="stars">{starsCount(props.data.stars)}</span>
            <span className="">
              <a href="#" className="add-button btn btn-primary text-right">
                Add Item
              </a>
            </span>
          </div>
        </div>
      </div>
    </div>
  );
}
