import Card from "./cards";

export default function App() {
  const price = [
    {
      plan: "Intro",
      price: "$19",
      desc: "for most business that want for optimize web queries",
      item: [
        "limited links",
        "No analytics platform",
        "Chat support",
        "Optimize hashtags",
        "150 users",
      ],
    },
    {
      plan: "Popular",
      price: "$39",
      desc: "for most business that want for optimize web queries",
      item: [
        "35 limited links",
        "Own analytics platform",
        "Chat support",
        "Optimize hashtags",
        "500 users",
      ],
    },
    {
      plan: "Enterprise",
      price: "$99",
      desc: "for most business that want for optimize web queries",
      item: [
        "All limited links",
        "Own analytics platform",
        "Chat and Email support",
        "Optimize hashtags",
        "Unlimited users",
      ],
    },
  ];
  return (
    <div class="container">
      <div class="row">
        <div class="col-lg-12">
          <h1 class="h3 my-2">Price Component</h1>
        </div>
      </div>
      <div class="row">
        {price.map((p) => {
          if (p.plan === "Intro") {
            return (
              <>
                <Card
                  plan={p.plan}
                  price={p.price}
                  desc={p.desc}
                  item={p.item.map((i) => {
                    return i;
                  })}
                ></Card>
              </>
            );
          } else if (p.plan === "Popular") {
            return (
              <>
                <Card
                  plan={p.plan}
                  price={p.price}
                  desc={p.desc}
                  item={p.item.map((i) => {
                    return i;
                  })}
                ></Card>
              </>
            );
          } else if (p.plan === "Enterprise") {
            return (
              <>
                <Card
                  plan={p.plan}
                  price={p.price}
                  desc={p.desc}
                  item={p.item.map((i) => {
                    return i;
                  })}
                ></Card>
              </>
            );
          }
        })}
      </div>
    </div>
  );
}
