import Card from "./cards";

export default function App() {
  const price = [
    {
      plan: "Free",
      price: 0,
      desc: "for small business that want for optimize web queries",
      item: [
        "Single User",
        "5GB Storage",
        "Unlimited Public Projects",
        "Community Access",
        "Unlimited Private Projects",
        "Dedicated Phone Support",
        "Free Subdomain",
        "Monthly Status Reports",
      ],
    },
    {
      plan: "Plus",
      price: 9,
      desc: "for medium size business that want for optimize web queries",
      item: [
        "5 Users",
        "50 GB Storage",
        "Unlimited Public Projects",
        "Community Access",
        "Unlimited Private Projects",
        "Dedicated Phone Support",
        "Free Subdomain",
        "Monthly Status Reports",
      ],
    },
    {
      plan: "Pro",
      price: "$49",
      desc: "for most business that want for optimize web queries",
      item: [
        "5 Users",
        "50 GB Storage",
        "Unlimited Public Projects",
        "Community Access",
        "Unlimited Private Projects",
        "Dedicated Phone Support",
        "Free Subdomain",
        "Monthly Status Reports",
      ],
    },
  ];
  return (
    <div class="container mt-4 bg-primary">
      <div class="row">
        <div class="col-lg-12">
          <h1 class="h3 my-3 text-white">Price Component</h1>
        </div>
      </div>
      <div class="row">
        {price.map((p) => {
          return (
            <Card
              plan={p.plan}
              price={p.price}
              desc={p.desc}
              item={p.item.map((i) => {
                return i;
              })}
            ></Card>
          );
        })}
      </div>
    </div>
  );
}
